import useForm from "../lib/useForm";
import Form from './styles/Form'

export default function CreateGuitar() {
  const { inputs, handleChange, clearForm, resetForm } = useForm({
    name: "Guitar Name",
    price: 100,
    description: "Brand type color",
  });

  return (
    <Form>
      <fieldset >
        <label htmlFor="name">
          Name
          <input
            type="text"
            id="name"
            name="name"
            placeholder="Name"
            value={inputs.name}
            onChange={handleChange}
          />
        </label>
        <label htmlFor="price">
          Price
          <input
            type="number"
            id="price"
            name="price"
            placeholder="Price"
            value={inputs.price}
            onChange={handleChange}
          />
        </label>
        <label htmlFor="description">
          Description
          <input
            type="text"
            id="description"
            name="description"
            placeholder="Description"
            value={inputs.description}
            onChange={handleChange}
          />
        </label>
      </fieldset>

      <button type="button">+ Add Guitar</button>

      {/* <button type="button" onClick={clearForm}>Clear Form</button>
      <button type="button" onClick={resetForm}>Reset Form</button> */}

    </Form>
  );
}
