import { useMutation } from "@apollo/client";
import gql from "graphql-tag";
import useForm from "../lib/useForm";
import Form from "./styles/Form";

const CREATE_GUITAR_MUTATION = gql`
  mutation CREATE_GUITAR_MUTATION(
    $name: String!
    $description: String!
    $price: Int!
    $image: Upload
  ){
    createGuitar(
      data: {
        name: $name
        description: $description
        price: $price
        status: "AVAILABLE"
        photo: {
          create: {
            image: $image,
            altText: $name
          }
        }
      }
    ) {
      id
      price
      description
      name
    }
  }
`;


export default function CreateGuitar() {
  const { inputs, handleChange, clearForm, resetForm } = useForm({
    name: "Guitar Name",
    price: 100,
    description: "ex. Brand, Type, Color",
    image: "",
  });

  const payload = useMutation(CREATE_GUITAR_MUTATION, {
    variables: inputs
  })

  return (
    <Form onSubmit={(e) => {
      e.preventDefault();
      console.log(inputs);
    }}>
      <fieldset>
        <label htmlFor="name">
          Name
          <input
            type="text"
            id="name"
            name="name"
            placeholder="Name"
            value={inputs.name}
            onChange={handleChange}
          />
        </label>
        <label htmlFor="price">
          Price
          <input
            type="number"
            id="price"
            name="price"
            placeholder="Price"
            value={inputs.price}
            onChange={handleChange}
          />
        </label>
        <label htmlFor="description">
          Description
          <textarea
            id="description"
            name="description"
            placeholder="Description"
            value={inputs.description}
            onChange={handleChange}
          />
        </label>
        <label htmlFor="image">
          Image
          <input type="file" id="image" name="image" onChange={handleChange} />
        </label>
      </fieldset>

      <button type="submit">+ Add Guitar</button>

      {/* <button type="button" onClick={clearForm}>Clear Form</button>
      <button type="button" onClick={resetForm}>Reset Form</button> */}
    </Form>
  );
}
